document.addEventListener("DOMContentLoaded",()=>{chrome.storage.sync.get(["org","repo","token","folderName"],e=>{document.getElementById("org").value=e.org||"",document.getElementById("repo").value=e.repo||"",document.getElementById("token").value=e.token||"",document.getElementById("folderName").value=e.folderName||""}),document.getElementById("syncButton").addEventListener("click",async()=>{const e=document.getElementById("org").value,t=document.getElementById("repo").value,o=document.getElementById("token").value,n=document.getElementById("folderName").value;chrome.storage.sync.set({org:e,repo:t,token:o,folderName:n}),chrome.runtime.sendMessage({action:"startSync",org:e,repo:t,token:o,folderName:n},e=>{document.getElementById("status").textContent=e.message})})});